<?php include_once "funciones.php"; $vdia=$_POST['dia']; $sql_consfhd="SELECT fecha FROM com_menuporfecha WHERE id = '".$vdia."'"; $rs_consfhd=mysqli_query($conexion,$sql_consfhd);$rw_consfhd=mysqli_fetch_assoc($rs_consfhd); $busquedadia="SELECT E.codigo , U.empresa, U.id AS iduser, M.id AS idmenu, F.menu, F.id, R.menuporfecha, U.`usuario` AS empleado, U.`nombre` AS nomusuario, M.`nombre` AS nommenu, E.`descripcion` AS empresa, F.`fecha` AS fechamenu, R.`registro` AS anotado FROM `com_anotaciones` R INNER JOIN `com_menuporfecha` F ON R.`menuporfecha` = F.`id` INNER JOIN `com_menus` M ON F.`menu` = M.`id` INNER JOIN `com_usuarios` U ON R.`usuario` = U.`id` INNER JOIN `com_empresas` E ON U.`empresa` = E.`codigo` AND F.`fecha` = '".$rw_consfhd['fecha']."'";echo anotadosmenu($busquedadia,false);$sql_obttotxfch="SELECT SUM(F.`precio`) AS total FROM `com_anotaciones` R INNER JOIN `com_menuporfecha` F ON R.`menuporfecha` = F.`id` WHERE F.`fecha` = '".$rw_consfhd['fecha']."';"; $rs_obttotxfch=mysqli_query($conexion,$sql_obttotxfch);$rw_obttotxfch=mysqli_fetch_assoc($rs_obttotxfch);if($rw_obttotxfch['total']!=null and !empty($rw_obttotxfch['total'])){echo "<td colspan='3' align='left' style='text-align: left;'>TOTAL: $<strong style=\"font-family: 'Roboto', sans-serif; font-size: larger;\">".$rw_obttotxfch['total']."</strong></td></tr>";}?>