<?php include_once "funciones.php";$vinicial=$_POST['ini'];$vfinal=$_POST['fin'];echo consxrg($vinicial,$vfinal); $sql_totxrg="SELECT SUM(`precio`) as total FROM `com_anotaciones` R INNER JOIN `com_menuporfecha` F ON R.`menuporfecha` = F.`id` AND F.`fecha` BETWEEN CAST('".$vinicial."' AS DATE ) AND CAST('".$vfinal."' AS DATE )"; $rs_totxrg=mysqli_query($conexion,$sql_totxrg);$rw_totxrg=mysqli_fetch_assoc($rs_totxrg);if($rw_totxrg['total']!=null and !empty($rw_totxrg['total'])){echo "<td colspan='3' align='left' style='text-align: left;'>TOTAL: $<strong style=\"font-family: 'Roboto', sans-serif; font-size: larger;\">".$rw_totxrg['total']."</strong></td></tr>";}?>