<?php include_once "funciones.php"; $vmn=$_POST['dmn']; $namesh=$_POST['shb'];$rangini=$_POST['rng1'];$rangfin=$_POST['rng2'];if(!empty($vmn) and empty($rangini) and empty($rangfin) or $rangini=='' or $rangfin==''){$sql_consxsh="SELECT fecha FROM com_menuporfecha WHERE id = '".$vmn."'";$rs_consxsh=mysqli_query($conexion,$sql_consxsh);$rw_consxsh=mysqli_fetch_assoc($rs_consxsh);$buscarmnxsh="SELECT E.codigo , U.empresa, U.id AS iduser, M.id AS idmenu, F.menu, F.id, R.menuporfecha, U.`usuario` AS empleado, U.`nombre` AS nomusuario, M.`nombre` AS nommenu, E.`descripcion` AS empresa, F.`fecha` AS fechamenu, R.`registro` AS anotado FROM `com_anotaciones` R INNER JOIN `com_menuporfecha` F ON R.`menuporfecha` = F.`id` INNER JOIN `com_menus` M ON F.`menu` = M.`id` INNER JOIN `com_usuarios` U ON R.`usuario` = U.`id` INNER JOIN `com_empresas` E ON U.`empresa` = E.`codigo` WHERE U.nombre LIKE '%".$namesh."%' AND F.`fecha` = '".$rw_consxsh['fecha']."' OR U.`usuario` LIKE '%".$namesh."%' AND F.`fecha` = '".$rw_consxsh['fecha']."';";$sh=true;echo anotadosmenu($buscarmnxsh,$sh);$sql_obttotxfch="SELECT SUM(`precio`) as total FROM `com_anotaciones` R INNER JOIN `com_menuporfecha` F ON R.`menuporfecha` = F.`id` INNER JOIN com_usuarios U ON R.usuario = U.id WHERE U.nombre LIKE '%".$namesh."%' AND F.`fecha` = '".$rw_consxsh['fecha']."' OR U.`usuario` LIKE '%".$namesh."%' AND F.`fecha` = '".$rw_consxsh['fecha']."';";$rs_obttotxfch=mysqli_query($conexion,$sql_obttotxfch);$rw_obttotxfch=mysqli_fetch_assoc($rs_obttotxfch);if($rw_obttotxfch['total']!=null and !empty($rw_obttotxfch['total'])){echo "<td colspan='3' align='left' style='text-align: left;'>TOTAL: $<strong style=\"font-family: 'Roboto', sans-serif; font-size: larger;\">".$rw_obttotxfch['total']."</strong></td></tr>";}}elseif(empty($vmn) and !empty($rangini) and !empty($rangfin) or $vmn=''){$busxrg="SELECT E.codigo , U.empresa, U.id AS iduser, M.id AS idmenu, F.menu, F.id, R.menuporfecha, U.`usuario` AS empleado, U.`nombre` AS nomusuario, M.`nombre` AS nommenu, E.`descripcion` AS empresa, F.`fecha` AS fechamenu, R.`registro` AS anotado FROM `com_anotaciones` R INNER JOIN `com_menuporfecha` F ON R.`menuporfecha` = F.`id` INNER JOIN `com_menus` M ON F.`menu` = M.`id` INNER JOIN `com_usuarios` U ON R.`usuario` = U.`id` INNER JOIN `com_empresas` E ON U.`empresa` = E.`codigo` WHERE U.nombre LIKE '%".$namesh."%' AND F.`fecha` BETWEEN CAST('".$rangini."' AS DATE) AND CAST('".$rangfin."' AS DATE) OR U.`usuario` LIKE '%".$namesh."%' AND F.`fecha` BETWEEN CAST('".$rangini."' AS DATE) AND CAST('".$rangfin."' AS DATE)"; $sh=true; echo anotadosmenu($busxrg,$sh); $sql_obttotxfch="SELECT SUM(`precio`) as total FROM `com_anotaciones` R INNER JOIN `com_menuporfecha` F ON R.`menuporfecha` = F.`id` INNER JOIN com_usuarios U ON R.usuario = U.id WHERE U.nombre LIKE '%".$namesh."%' AND F.`fecha` BETWEEN CAST('".$rangini."' AS DATE) AND CAST('".$rangfin."' AS DATE) OR U.`usuario` LIKE '%".$namesh."%' AND F.`fecha` BETWEEN CAST('".$rangini."' AS DATE) AND CAST('".$rangfin."' AS DATE);";$rs_obttotxfch=mysqli_query($conexion,$sql_obttotxfch);$rw_obttotxfch=mysqli_fetch_assoc($rs_obttotxfch);if($rw_obttotxfch['total']!=null and !empty($rw_obttotxfch['total'])){echo "<td colspan='3' align='left' style='text-align: left;'>TOTAL: $<strong style=\"font-family: 'Roboto', sans-serif; font-size: larger;\">".$rw_obttotxfch['total']."</strong></td></tr>";}}?>