<?php  include_once "funciones.php"; $vvst=$_POST['dvst'];$sql_convstdy="SELECT fecha FROM com_menuporfecha WHERE id = '".$vvst."'";$rs_convstdy=mysqli_query($conexion,$sql_convstdy);$rw_convstdy=mysqli_fetch_assoc($rs_convstdy);$busvst="SELECT M.`nombre`, F.`fecha`, V.`cantidad`, V.`precio`, (V.`cantidad` * V.`precio`) AS importe, V.`registro` FROM `com_visitantes` V INNER JOIN `com_menuporfecha` F ON V.`menuporfecha` = F.`id` INNER JOIN `com_menus` M ON F.`menu` = M.`id` WHERE F.`fecha` = '".$rw_convstdy['fecha']."'";echo consvisitsmnu($busvst,'');$sql_obtpltrg="SELECT F.`fecha`, SUM(V.`cantidad`) AS platillos, SUM(V.`cantidad` * V.`precio`) AS total FROM `com_visitantes` V INNER JOIN `com_menuporfecha` F ON V.`menuporfecha` = F.`id` WHERE F.`fecha` = '".$rw_convstdy['fecha']."';";$rs_obtpltrg=mysqli_query($conexion,$sql_obtpltrg);$tot_obtpltrg=mysqli_num_rows($rs_obtpltrg);$rw_obtpltrg=mysqli_fetch_assoc($rs_obtpltrg);if($tot_obtpltrg>0 and $rw_obtpltrg['platillos']!=null){echo "<tr><td colspan='2'>CARGOS: <strong style=\"font-family: 'Roboto', sans-serif; font-size: larger;\">".$tot_obtpltrg."</strong></td><td colspan='2'>PLATILLOS: <strong style=\"font-family: 'Roboto', sans-serif; font-size: larger;\">".$rw_obtpltrg['platillos']."</strong></td><td colspan='2'>TOTAL: $<strong style=\"font-family: 'Roboto', sans-serif; font-size: larger;\">".$rw_obtpltrg['total']."</strong></td></tr>";}?>