<?php include_once "funciones.php"; $periodo=$_POST['idper']; if($conexion){$sql_periodos="SELECT pendientes,vigencia FROM vac_periodos WHERE id = '".$periodo."'";$rs_periodos=mysqli_query($conexion,$sql_periodos);$rw_periodos=mysqli_fetch_assoc($rs_periodos);$totalpendientes=$rw_periodos['pendientes'];$vigencia=$rw_periodos['vigencia']; $soldays=retcantdayssols($periodo,$_SESSION['idloguser']); $inactivar=intval($totalpendientes)-intval($soldays); echo "<option data-icon='fa fa-calendar' value='0'>Selecciona dias a disfrutar</option>";for($x=1;$x<=$totalpendientes;$x++){echo "<option value='".$x."' ";if($x>=(intval($inactivar)+1)){echo "disabled='disabled'";}echo ">".$x."</option>";if($x==$inactivar){echo "<option data-divider='true'></option>";}}echo ",".$vigencia;}?>